SpectralCanvasProEditor::SpectralCanvasProEditor(SpectralCanvasProProcessor& processor)
    : AudioProcessorEditor(&processor), processor_(processor)
{
    // Set initial size (Phase 2 will make this resizable)
    setSize(500, 400);

    // Initialize layout areas immediately after setting size
    resized();

    // Start timer for UI updates at 30 FPS
    startTimerHz(30);

    // Initialize fonts
    labelFont_ = juce::Font{juce::FontOptions{12.0f, juce::Font::bold}};
    valueFont_ = juce::Font{juce::FontOptions{10.0f}};

    // Set initial paint color based on mode
    paintColour_ = juce::Colour::fromHSV(0.0f, 0.8f, 0.9f, 1.0f); // Red-orange
}

