    43‚Üí// RT-safe 64-byte aligned allocator for SIMD optimization
    44‚Üítemplate<typename T>
    45‚Üíclass AlignedAllocator {
    46‚Üípublic:
    47‚Üí    using value_type = T;
    48‚Üí    static constexpr size_t Alignment = 64;
    49‚Üí    
    50‚Üí    T* allocate(size_t n) {
    51‚Üí        void* ptr = nullptr;
    52‚Üí        #ifdef _WIN32
    53‚Üí        ptr = _aligned_malloc(n * sizeof(T), Alignment);
    54‚Üí        #else
    55‚Üí        if (posix_memalign(&ptr, Alignment, n * sizeof(T)) != 0) ptr = nullptr;
    56‚Üí        #endif
    57‚Üí        if (!ptr) throw std::bad_alloc();
    58‚Üí        return static_cast<T*>(ptr);
    59‚Üí    }
    60‚Üí    
    61‚Üí    void deallocate(T* ptr, size_t) noexcept {
    62‚Üí        #ifdef _WIN32
    63‚Üí        _aligned_free(ptr);
    64‚Üí        #else
    65‚Üí        free(ptr);
    66‚Üí        #endif
    67‚Üí    }
    68‚Üí};
    69‚Üí
