#pragma once

#include <cstdint>

namespace SpectralCanvas {

/**
 * MaskColumn - Paint event data structure
 * 
 * Represents a single paint stroke column for RT-safe communication
 * between UI thread and audio thread. Designed to be trivially copyable
 * for use in lock-free queues.
 */
struct MaskColumn {
    // Column index in the spectral grid
    int32_t columnIndex = -1;

    // Paint intensity [0.0, 1.0] where 1.0 = full intensity
    float intensity = 0.0f;

    // Frequency bin range for this column
    int32_t startBin = 0;
    int32_t endBin = 0;

    // Timestamp when paint event was created (for aging/decay)
    uint64_t timestamp = 0;

    // Paint type/mode for future extensions
    uint8_t paintType = 0;

    // Reserved for alignment and future use
    uint8_t reserved[3] = {0, 0, 0};

    // Default constructor
    MaskColumn() = default;

    // Explicit constructor for paint events
    MaskColumn(int32_t col, float intensity, int32_t startBin, int32_t endBin, uint64_t timestamp)
        : columnIndex(col)
        , intensity(intensity)
        , startBin(startBin)
        , endBin(endBin)
        , timestamp(timestamp)
        , paintType(0)
        , reserved{0, 0, 0}
    {}

    // Check if this is a valid paint event
    bool isValid() const noexcept {
        return columnIndex >= 0 && intensity > 0.0f && startBin < endBin;
    }

    // Check if paint event has expired (for decay systems)
    bool isExpired(uint64_t currentTime, uint64_t maxAge) const noexcept {
        return currentTime > timestamp && (currentTime - timestamp) > maxAge;
    }
};

// Ensure the structure is trivially copyable for lock-free operations
static_assert(std::is_trivially_copyable_v<MaskColumn>, 
              "MaskColumn must be trivially copyable for RT-safe queues");

// Size check - keep it cache-line friendly
static_assert(sizeof(MaskColumn) <= 32, 
              "MaskColumn should fit in 32 bytes for cache efficiency");

} // namespace SpectralCanvas
