+    const int N = 1024;
+    fft_.reset(new juce::dsp::FFT(10)); // log2(1024)
+    fftBuffer_.assign(N, 0.0f);
+    olaBuffer_.assign(N + 3 * (N/4), 0.0f); // overlap=4
+    analysisWin_.resize(N); synthWin_.resize(N);
+    juce::dsp::WindowingFunction<float> win(N, juce::dsp::WindowingFunction<float>::hann);
+    std::vector<float> tmp(N); win.fillWindowingTables(tmp.data(), N);
+    for (int n=0; n<N; ++n) { float w = std::sqrt(tmp[n]); analysisWin_[n]=w; synthWin_[n]=w; }
+
+    spectralMask_.assign(N/2+1, 1.0f);
+    spectralMaskSmoothed_ = spectralMask_;
+    const float hopSec = (N/4) / sampleRate;
+    decayCoeff_ = std::exp(-hopSec * std::log(1000.0f) / 0.6f); // T60=0.6s
 }

 void SpectralPaintProcessor::process (juce::AudioBuffer<float>& buffer)
