+                for (int i=0;i<N;++i) fftBuffer_[i] = inBuf[i]*analysisWin_[i];
+                fft_->performRealOnlyForwardTransform(fftBuffer_.data());
+                decayMaskTowardUnity();
+                smoothMask();
+                applyMaskToSpectrum();
+                fft_->performRealOnlyInverseTransform(fftBuffer_.data());
+                const float invN=1.0f/N;
+                for (int i=0;i<N;++i){
+                    int idx=(olaWritePos_+i)%olaBuffer_.size();
+                    olaBuffer_[idx]+=fftBuffer_[i]*synthWin_[i]*invN;
+                }
+                olaWritePos_=(olaWritePos_+H)%olaBuffer_.size();
+                inCount=0;
+            }
+            float y=olaBuffer_[olaReadPos_];
+            olaBuffer_[olaReadPos_]=0.0f;
+            olaReadPos_=(olaReadPos_+1)%olaBuffer_.size();
+            data[n]=juce::jlimit(-2.0f,2.0f,y * juce::Decibels::decibelsToGain(-6.0f)); // âˆ’6dB trim
+        }
