+void SpectralPaintProcessor::decayMaskTowardUnity(){
+    for (auto& v: spectralMask_) v = 1.0f + (v-1.0f)*decayCoeff_;
+}
+void SpectralPaintProcessor::smoothMask(){
+    const int K=spectralMask_.size();
+    float hopSec= (1024/4)/44100.0f;
+    float aAtk=std::exp(-hopSec/0.003f), aRel=std::exp(-hopSec/0.08f);
+    for(int k=0;k<K;++k){
+        float t=juce::jlimit(0.1f,1.0f,spectralMask_[k]);
+        float y=spectralMaskSmoothed_[k];
+        spectralMaskSmoothed_[k]= (t<y)? aAtk*y+(1-aAtk)*t : aRel*y+(1-aRel)*t;
+    }
+    float minv=1.0f; for(float v: spectralMaskSmoothed_) minv=std::min(minv,v);
+    peakGrDb_.store(juce::Decibels::gainToDecibels(minv));
+}
+void SpectralPaintProcessor::applyMaskToSpectrum(){
+    const int N=1024,K=N/2;
+    for(int k=0;k<=K;++k){
+        float re,im;
+        if(k==0){re=fftBuffer_[0];im=0;}
+        else if(k==K){re=fftBuffer_[1];im=0;}
+        else{re=fftBuffer_[2*k];im=fftBuffer_[2*k+1];}
+        float g=spectralMaskSmoothed_[k];
+        re*=g; im*=g;
+        if(k==0) fftBuffer_[0]=re;
+        else if(k==K) fftBuffer_[1]=re;
+        else{fftBuffer_[2*k]=re; fftBuffer_[2*k+1]=im;}
+    }
+}
