#pragma once

namespace SpectralCanvas {

/**
 * Phase 1 Runtime Parameters - Tunable without recompile
 * 
 * These parameters control the clean mixer effect behavior
 * and can be exposed as plugin parameters for user control.
 */
struct Params
{
    // Output processing
    float outputTrimDb = -6.0f;         // Headroom trim (prevents clipping)

    // Spectral mask behavior
    float maskFloor = 0.1f;             // Minimum attenuation (-20dB)
    float T60DecaySeconds = 0.6f;       // Time for mask to decay to unity

    // Paint brush characteristics  
    float brushWidth = 3.0f;            // Frequency spread in bins
    float brushStrength = 1.0f;         // Intensity multiplier (0-2 range)

    // Attack/Release smoothing
    float attackMs = 3.0f;              // Fast attack for cutting
    float releaseMs = 80.0f;            // Slower release for transparency

    // Safety limits
    float maxOutputLevel = 0.7f;        // Soft clipper threshold (-3dB)
    float olaClampRange = 4.0f;         // OLA accumulator clamp (Â±4.0)

    // Default constructor ensures safe values
    Params() = default;

    // Validation helpers
    bool isValid() const noexcept
    {
        return outputTrimDb >= -20.0f && outputTrimDb <= 6.0f &&
               maskFloor >= 0.01f && maskFloor <= 1.0f &&
               T60DecaySeconds >= 0.1f && T60DecaySeconds <= 5.0f &&
               brushWidth >= 1.0f && brushWidth <= 50.0f &&
               brushStrength >= 0.0f && brushStrength <= 2.0f;
    }

    void clampToValid() noexcept
    {
        outputTrimDb = juce::jlimit(-20.0f, 6.0f, outputTrimDb);
        maskFloor = juce::jlimit(0.01f, 1.0f, maskFloor);
        T60DecaySeconds = juce::jlimit(0.1f, 5.0f, T60DecaySeconds);
        brushWidth = juce::jlimit(1.0f, 50.0f, brushWidth);
        brushStrength = juce::jlimit(0.0f, 2.0f, brushStrength);
    }
};

} // namespace SpectralCanvas

